import React, { useEffect, useState } from 'react';
import { View, Text, Button, StyleSheet, StatusBar } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { useFocusEffect } from '@react-navigation/native';


const DeliveryScreen = ({ navigation, route }) => {

  const [info, setInfo] = useState(null);


  const delivery  = route.params.delivery;

   // fetch data from the API
   useFocusEffect(
    React.useCallback(() => {

      async function getRiderInfo() {
        let data = await AsyncStorage.getItem("user")
        data = JSON.parse(data)
        fetchData(data)
      }

      getRiderInfo()

      async function fetchData(token) {

        console.log("token",token)

        fetch(
          `http://localhost:6869/deliveries/delivery?id=${delivery.id}`,
          {
            headers: { 
                       'Access-Control-Allow-Origin': '*',
                       'Authorization' : 'Bearer ' + token,
                      }
          }
        )
          .then(response => response.json())
          .then(data => {
            console.log("DATAAAAAAAAAAAAAAA", data)
            setInfo(data)
          })
      }

    }, [])
  );

  console.log(info);

  return (
    <View style={styles.container}>

      data ?

        <Text style={styles.title}>Delivery No.: {info.delivery.id}</Text>
        <Text style={styles.info}>Store: {info.delivery.store.name}</Text>
        <Text style={styles.info}>Store address: {info.delivery.store.latitude}</Text>
        <Text style={styles.info}>Status: {info.event.status}</Text>
        <Text style={styles.info}>Date: {new Date(info.event.timestamp).format("dd/mm/yyyy hh:MM:ss")}</Text>

        : 

        <Text>No data</Text>

    </View>
  );
};


export default DeliveryScreen;

const styles = StyleSheet.create({
  title: {
    fontSize: 32,
    marginBottom: 20,
  },
  info: {
    fontSize: 20,
    alignItems: 'center',
    justifyContent: 'center',
  },
  container: {
      flex: 1,
      padding: 20,
      paddingTop: 20,
      paddingBottom: 20,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
  }
});
