import React from 'react';
import { View, Text, Button, StyleSheet, TouchableOpacity } from 'react-native';

const DeliveryScreen = ({ navigation, route }) => {

  const delivery  = route.params.delivery;

   // fetch data from the API
   useFocusEffect(
    React.useCallback(() => {

      async function getRiderInfo() {
        let data = await AsyncStorage.getItem("user")
        data = JSON.parse(data)
        fetchData(data.token)
      }

      getRiderInfo()

      async function fetchData(token) {

        fetch(
          `http://localhost:6869/deliveries/delivery`,
          {
            headers: { 
                       'Access-Control-Allow-Origin': '*',
                       'Authorization' : 'Bearer ' + token,
                      }
          }
        )
          .then(response => response.json())
          .then(data => {
            console.log("deliveries", data)
            setDeliveries(data)
          })
          .catch((reason) => {
            setError(true)
          })
      }

    }, [])
  );

  return (
    <View style={styles.container}>
        <Text style={styles.title}>Delivery No.: {delivery.id}</Text>
        <Text style={styles.title}>Store: {delivery.store.name}</Text>
        <Text style={styles.title}>Store address: {delivery.store.latitude}</Text>
    </View>
  );
};


export default DeliveryScreen;

const styles = StyleSheet.create({
  title: {
    fontSize: 32
  },
  container: {
      flex: 1,
      padding: 20,
      paddingTop: 20,
      paddingBottom: 20,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
  }
});
