import React from 'react';
import { View, Text, Button, StyleSheet, TouchableOpacity } from 'react-native';


const pendentDeliveries = [
  {
    id: 1,
    color: "blue",
    text: "Request n1"
  },
  {
    id: 2,
    color: "red",
    text: "Request n8"
  },
];

const DeliveriesScreen = () => {

  const [deliveries, setDeliveries] = useState(null);
  const [error, setError] = useState(false);

  // fetch data from the API
  useFocusEffect(
    React.useCallback(() => {

      fetch(
        `$http://localhost:8080/deliveries/deliveriesByStatus?status=PENDING`,
        {
            headers: {'Access-Control-Allow-Origin': '*'}
        }
      )
      .then(response => response.json())
      .then(data => {
          setDeliveries(data)
      })
      .catch((reason) => {
        setError(true)
      })
    }, [])
  );

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Pendent Deliveries</Text>



      {pendentDeliveries.map((prop, key) => {
        return (
          <Text style={{backgroundColor : prop.color}}>{prop.text}</Text>
        );
      })}
      <TouchableOpacity style={styles.loginBtn}>
        <Text style={styles.loginText} onPress={() => navigation.navigate('Delivery')}>Delivery No929I293I23</Text>
      </TouchableOpacity>
    </View>

  );
};

export default DeliveriesScreen;

const styles = StyleSheet.create({
  title: {
    fontSize: 32
  },
  container: {
    flex: 1,
    padding: 20,
    paddingTop: 20,
    paddingBottom: 20,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  }
});
